      tmp_2 &amp;#x3D; llvm::BinaryOperator::getOperand(opcode, 1u);
      if ( (llvm::isa&amp;lt;llvm::Constant,llvm::Value *&amp;gt;(&amp;amp;tmp_1) &amp;amp; 1) !&amp;#x3D; 0 )
